import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import Cabecalho from './components/Cabecalho/Cabecalho';
import Login from './components/Login/Login';
import './App.scss';

function App() {
  const [usuario, setUsuario] = useState(JSON.parse(sessionStorage.getItem('usuario')) || null);

  const handleLogin = (user) => {
    setUsuario(user);
  };

  const handleLogout = () => {
    // Limpa o sessionStorage e redefine o estado do usuário para null
    sessionStorage.removeItem('usuario');
    setUsuario(null);
  };

  return (
    <Router>
      <div className="app-container">
        <Cabecalho usuario={usuario} />

        <Route path="/login">
          {usuario ? <Redirect to="/" /> : <Login onLogin={handleLogin} />}
        </Route>

        {/* Adicione mais rotas aqui conforme necessário */}

        <Route exact path="/">
          {usuario ? (
            <div>
              {/* Conteúdo da tela principal */}
              <h2>Bem-vindo à tela principal!</h2>
              <button onClick={handleLogout}>Logout</button>
            </div>
          ) : (
            <Redirect to="/login" />
          )}
        </Route>
      </div>
    </Router>
  );
}

export default App;
