// src/components/Login.js
import React, { useState } from 'react';

const Login = ({ history }) => {
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const handleLogin = async () => {
    try {
      // Fazer uma requisição para o servidor JSON-SERVER para autenticação
      const response = await fetch('http://localhost:3001/usuarios');
      const usuarios = await response.json();

      const usuarioAutenticado = usuarios.find(
        (usuario) => usuario.email === email && usuario.senha === senha
      );

      if (usuarioAutenticado) {
        // Armazenar dados do usuário no sessionStorage
        sessionStorage.setItem('usuario', JSON.stringify(usuarioAutenticado));

        // Redirecionar para a tela principal
        history.push('/');
      } else {
        alert('Usuário não autenticado. Verifique o email e senha.');
      }
    } catch (error) {
      console.error('Erro ao autenticar usuário:', error);
    }
  };

  return (
    <div>
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
      <input type="password" value={senha} onChange={(e) => setSenha(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
