// src/components/Login/Login.js
import React, { useState } from 'react';
import { Redirect } from 'react-router-dom'; // Importe o Redirect
import './Login.scss';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginError, setLoginError] = useState('');
  const [redirectToDashboard, setRedirectToDashboard] = useState(false);

  const handleLogin = async () => {
    try {
      const response = await fetch('caminho/para/seu/dados.json');
      const data = await response.json();

      const user = data.find((user) => user.username === username && user.password === password);

      if (user) {
        console.log('Usuário autenticado!');
        setLoginError('');
        setRedirectToDashboard(true); // Define redirectToDashboard como true após o login bem-sucedido
      } else {
        console.log('Usuário não autenticado!');
        setLoginError('Usuário ou senha incorretos');
      }
    } catch (error) {
      console.error('Erro ao autenticar:', error);
      setLoginError('Erro ao autenticar');
    }
  };

  if (redirectToDashboard) {
    return <Redirect to="/dashboard" />; // Redireciona para a página /dashboard
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <label>Usuário:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
          <label>Senha:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button type="button" onClick={handleLogin}>
            Login
          </button>
          {loginError && <p className="error-message">{loginError}</p>}
        </form>
      </div>
    </div>
  );
};

export default Login;
