import React, { useState } from 'react';

const Login = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Simulação de uma lógica de autenticação
    const userData = {
      nome: 'Nome do Usuário',
      email: 'usuario@email.com',
    };

    // Simulação de uma lógica de autenticação bem-sucedida
    if (username === 'usuario' && password === 'senha') {
      // Configuração dos dados do usuário no sessionStorage
      sessionStorage.setItem('usuario', JSON.stringify(userData));

      // Chamada da função de sucesso de login passada como propriedade
      onLoginSuccess();
    } else {
      console.log('Login falhou. Usuário ou senha incorretos.');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <label>Usuário:</label>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      <label>Senha:</label>
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default Login;
