// src/components/Login.js
import React, { useState } from 'react';
import './Login.scss';

const Login = ({ onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState();

  const handleLogin = () => {
    // Simulação de uma lógica de autenticação
    const userData = [
      {
        "id": 1,
        "usuario": "RM98919",
        "senha": "123456"
      },
      {
        "id": 2,
        "usuario": "RM550154",
        "senha": "123456"
      }
    ];

    const user = userData.find(user => user.usuario === username && user.senha === password);

    if (user) {
      console.log('Login bem-sucedido!');
      // Chama a função de redirecionamento passada como propriedade
      onLoginSuccess();
    } else {
      console.log('Login falhou. Usuário ou senha incorretos.');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <form>
          <label>Usuário:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
          <label>Senha:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          <button type="button" onClick={handleLogin}>
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
