import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Cabecalho from './components/Cabecalho';
import Login from './components/Login';
import Home from './components/Home';
import './App.css';

const isAuthenticated = () => {
  // Verifica se o usuário está autenticado
  return sessionStorage.getItem('token'); // Substitua 'token' pelo nome do seu item de autenticação
};

const PrivateRoute = ({ element }) => {
  // Componente de rota privada para proteger o acesso à rota Home
  return isAuthenticated() ? element : <Navigate to="/" />;
};

const App = () => {
  const handleLogout = () => {
    // Função de logout
    sessionStorage.clear();
    window.location.href = '/';
  };

  return (
    <Router>
      <div>
        <Cabecalho />
        <Routes>
          <Route path="/" element={<Login />} />
          <Route
            path="/home"
            element={<PrivateRoute element={<Home />} />} // Protege a rota Home com PrivateRoute
          />
        </Routes>
        {isAuthenticated() && <button onClick={handleLogout}>Logout</button>}
      </div>
    </Router>
  );
};

export default App;
