import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';

const Login = () => {
  // Estado para armazenar os dados do formulário
  const [formData, setFormData] = useState({
    email: '',
    senha: '',
  });

  // Estado para controlar o redirecionamento após o login
  const [redirect, setRedirect] = useState(false);

  // Função para lidar com as mudanças nos campos do formulário
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();

    // Implemente aqui a lógica de autenticação com a API-JSON

    // Exemplo de redirecionamento após o login
    setRedirect(true);
  };

  // Redirecionar após o login
  if (redirect) {
    return <Redirect to="/pagina-inicial" />;
  }

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <label>
          Senha:
          <input
            type="password"
            name="senha"
            value={formData.senha}
            onChange={handleInputChange}
            required
          />
        </label>
        <br />
        <button type="submit">Entrar</button>
      </form>
    </div>
  );
};

export default Login;
